/** @format */

import React, {useState} from "react";

const Input = ({ setAddress, setCountry, world}) => {

	const [state, setState] = useState("Load");
	const india = {
		cname: "India",
		cheight: "65%%",
		cflag:
			"https://upload.wikimedia.org/wikipedia/en/thumb/4/41/Flag_of_India.svg/1200px-Flag_of_India.svg.png",
		ccurrency: "Rupees",
		cspeed: "km/hr",
		cdistance: "meter",
		cvolume: "litre",
		cweight: "gram",
		ctime: "IST",
	};
	const uk = {
		cname: "UK",
		cheight: "65%%",
		cflag:
			"https://upload.wikimedia.org/wikipedia/en/thumb/a/ae/Flag_of_the_United_Kingdom.svg/1200px-Flag_of_the_United_Kingdom.svg.png",
		ccurrency: "Sterling",
		cspeed: "mph",
		cdistance: "meter",
		cvolume: "gallons",
		cweight: "pound",
		ctime: "GMT",
	};
	const usa = {
		cname: "USA",
		cheight: "65%%",
		cflag:
			"https://upload.wikimedia.org/wikipedia/en/thumb/a/a4/Flag_of_the_United_States.svg/1200px-Flag_of_the_United_States.svg.png",
		ccurrency: "Dollar",
		cspeed: "mph",
		cdistance: "yard",
		cvolume: "litre",
		cweight: "gram",
		ctime: "IST",
	};

	const func = place => {
		console.log(place);
		if (place === "0"){
			setState("Load")
			console.log("World");
			setAddress(
				`https://www.openstreetmap.org/export/embed.html?bbox=-220.4296875%2C-39.90973623453719%2C171.91406250000003%2C84.26717240431665&amp;layer=cyclemap`
			);
			setCountry(world);
		}
		else if (place === "1"){
			setState("World");
			console.log("India");
			setAddress(
				`https://www.openstreetmap.org/export/embed.html?bbox=65.96191406250001%2C8.754794702435618%2C99.62402343750001%2C33.578014746144014&amp;layer=cyclemap`
			);
			setCountry(india)
		}
		else if (place === "2"){
			console.log("UK");
			setState("World");
			setAddress(
				`https://www.openstreetmap.org/export/embed.html?bbox=-12.458496093750002%2C49.97948776108648%2C4.372558593750001%2C57.868131763328826&amp;layer=cyclemap`
			);}
		else{
			console.log("USA");
			setState("World");
			setAddress(
				`https://www.openstreetmap.org/export/embed.html?bbox=-183.515625%2C6.664607562172573%2C-48.86718750000001%2C74.4964131169431&amp;layer=cyclemap`
			);}
		
	};
	
	let curr="0";
	return (
		<>
			<button
				style={{
					height: "30px",
					width: "100px",
					zIndex: "4",
					position: "absolute",
					top: "10vh",
					left: "54vw",
					borderRadius: "8px",
					outline: "none",
				}}
				onClick={()=>{func(curr)}}
				>
				{state}
			</button>
			<select
				className='btn'
				style={{
					height: "30px",
					width: "100px",
					zIndex: "4",
					position: "absolute",
					top: "10vh",
					left: "46vw",
					borderRadius: "8px",
					outline: "none",
					defaultValue: "World",
				}}
				onChange={(e)=>{curr = e.target.value}}
				className='form-control form-control-lg'>
				<option value='1'>India</option>
				<option value='2' >
					UK
				</option>
				<option value='3' >
					USA
				</option>
				<option selected value='0' >
					World
				</option>
			</select>
		</>
	);
};

export default Input;
